@import './variables';

* {
    margin: 0px;
    padding: 0px;
    box-sizing: border-box;
}

html {
    scroll-behavior: smooth;
    scroll-snap-type: y mandatory;
}

body{
    background-color: black;
    color: $white;
    font-family: 'Slabo 27px';
    font-size: 12pt;
    line-height: 2;
    text-align: center;
    overflow-x: hidden;
}

nav {
    height: 20vh;
    width: 100vw;
    display: flex;
    align-items: center;
    justify-content: center;
    div {
        width: 90vw;
        display: flex;
        justify-content: flex-end;
        button {
            width: 10vw;
            height: 10vh;
            padding-right: 10px;
            border-style: none;
            background: $green;
        }
    }
}

#book {
    height: auto;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: space-between;
    padding: 1em;
    form {
        width: 100%;
        font-size: 12pt;
        text-align: center;
        display: flex;
        flex-direction: column;
        align-items: center;
        flex-direction: space-evenly;
        input {
            font-family: 'Slabo 27px';
            text-align: center;
            padding: 1vw;
            margin: 1vw;
            width: 45vw;
        }
        input[type="radio"] {
            opacity: 0;
            height: 0;
        }
        #time {
            width: 45vw;                       
            span {
                background: $white;
                cursor: pointer;
                margin: 1vh 0;
                display: block;
                color: black;
                &:nth-child(1) {
                    background: $green;
                }
            }

            label {
                background: $white;
                color: black;
                display: block;
                margin: 1vh 0;
                text-align: center;
            }

        }
        #message {
            textarea {
            @extend input[type=text];
            height: 10vh;
            resize: vertical;
            width: 45vw;
            font-family: 'Slabo 27px';
            text-align: center;
            }
        }
        input[type=submit] {
            font-family: 'Slabo 27px';
            padding: 1em;
            border-style: none;
            outline: none;
            background: $green;
            cursor: pointer;
        }
        #submit-message {
            color: $green;
            opacity: 0;
        }
    }
}

@media (min-width: 500px) {
    #location {
        form {
        width: 60vw;
            input[type=text], 
            textarea {
            width: 58vw;
            }
        }
    }
}

