@import url('https://fonts.googleapis.com/css2?family=Source+Sans+Pro&display=swap');

$white: #f5f5f5;
$green: #36970f;
$darkGreen: #205909;

$desktop: 900px;
$tablet: 500px;

@mixin flexCenter($direction) {
	display: flex;
	align-items: center;
	justify-content: center;
	flex-direction: $direction;
}

@mixin fontSize {
	font-size: 20px;
	line-height: 1.2;
}

@mixin media($px) {
	@media (min-width: $px) {
		@content;
	}
}

@mixin borderRadius {
	border-radius: 1px;
}

