	padding: 1em;
	border-style: none;
	background: $green;
	cursor: pointer;
	&:hover {
		background: $darkGreen;
	}
}

@import "navbar";
@import "home";
@import "about";
@import "menu";
@import "review";
@import "location";
@import "footer";
