@import './variables';

@mixin selectionBox {
    width: 75vw;
        @include media($desktop) {
            width: 40vw;
        }
    label {
        @include flexCenter(row);
        width: inherit;
        background: $white;
        color: black;
        height: 8vh;
        margin: 2vh 0;
        text-align: center;
        cursor: pointer;
        &:hover {
            background: $green;
        }
    }
    input[type="radio"] {
        position: absolute;
        opacity: 0;
        height: 0;
        width: 0;
    }
}

* {
    margin: 0px;
    padding: 0px;
    box-sizing: border-box;
}

body{
    background: #000;
    color: $white;
    font-family: 'Slabo 27px', serif;
    min-height: 100vh;
    @include fontSize;
    line-height: 2;
    text-align: center;
    overflow-x: hidden;
    @include media($desktop) {
        background-image: url(/images/burger-bg.jpeg);
        background-repeat: no-repeat no-repeat;
        background-size: cover;
        background-position: center;
        }
}

:focus {
    outline-color: $darkgreen;
}

button {
    padding: 1em;
    border-style: none;
    background: $green;
    font-family: 'Slabo 27px', serif;
    @include fontSize;
    cursor: pointer;
    &:hover {
        background: $darkgreen;
    }
}

nav {
    background: #000;
    width: 100vw;
    padding: 0 2vw;
    height: 10vh;
    display: flex;
    align-items: center;
    justify-content: space-between;    
    img {
        width: auto;
        height: 10vh;
    }
    #backBtn {
        display: flex;
        align-items: center;
        justify-content: center;
        text-shadow: 1px 1px 2px black;
        background: $green;
        color: $white;
        min-width: 10vw;
        text-decoration: none;
        padding: 0 2vw;
        width: auto;
        margin-right: 1vw;
        border-radius: 15px;
        &:hover {
            background: $darkgreen;
        }
    }
}



#book {
    background: #000;
    margin: auto;
    width: 95vw; 
    @include media($desktop) {
        margin-top: 10vh;
        width: 60vw;
    }
    height: auto;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: space-between;
    padding: 1em;
    form {
        text-align: center;
        display: flex;
        flex-direction: column;
        align-items: center;
        flex-direction: space-evenly;
        div {
            width: 80vw;
            @include media($desktop) {
                width: 45vw;
            }
        }
        h3::after {
            content: ' * ';
            color: $green;
        }
        input{
            font-family: 'Slabo 27px';
            @include fontSize;
            text-align: center;
            height: 8vh;
            padding: 2vw;
            margin: 2vw;
            width: 75vw;
            @include media($desktop) {
                padding: 1vw;
                margin: 1vw;
                width: 40vw;
            }
            background: $white;
        }
        .invalid {
            border: 2px solid red;
        }
        .error {
            display: none;
            color: red;
        }
        .restaurant {
            @include selectionBox();
        }
        .time {
            @include selectionBox();
            h3 {
                height: 8vh;
            }                  
            #hour-selection {
                @include selectionBox();
                span {
                   @extend label;
                }
            }
            #time-selection {
                display: none;
                position: relative;
                button {
                    width: 75vw;
                    padding: 0.6em;
                    @include media($desktop) {
                        position: absolute;
                        top: -9vh;
                        right: 0;
                        width: 7vw;
                    }
                }   
            }    
        }
        .party {
            @include selectionBox();
        }
        .message {
            label {
                h3:after {
                    content: '';
                }
            }
            textarea {
            @extend input;
            height: 30vh;
            resize: vertical;
            width: 75vw;
            @include media($desktop) {
                width: 40vw;
            }
            text-align: center;
            background: $white;
            color: black;
            }
        }
        input[type=submit] {
            @extend button;
            height: auto;
            padding: 1em;
            width: 75vw;
            border-radius: 15px;
            @include media($desktop) {
                width: auto;
            }
        }
    }
}

